    public String getRouteInformation(String name) {
        return routes.get(name);
    }

    public void setNewSession() {
        session = new ArrayList<Pinpoint>();
    }

    public void addPinpointToSession(Pinpoint p) {
        session.add(p);
    }

    public List<RoutePoint> allRoutePoints() {
        List<RoutePoint> allRoutePoints = new ArrayList<>();
        SQLiteDatabase db = getReadableDatabase();
        Cursor cursor = db.rawQuery("SELECT * FROM " + ROUTEPOINTTABLE, null);
        if (cursor != null && cursor.moveToFirst()) {
            do {
                int id = cursor.getInt(0);
                float lat = cursor.getFloat(1);
                float lon = cursor.getFloat(2);
                String name = cursor.getString(3);
                String desc = cursor.getString(4);
                RoutePoint point = new RoutePoint(id, name, desc, lon, lat, false);
                allRoutePoints.add(point);
            } while(cursor.moveToNext());
            cursor.close();
        }
        db.close();
        return allRoutePoints;
    }

    public List<BlindWallPoint> allBlindWallPoints() {
        List<BlindWallPoint> points = new ArrayList<>();
        SQLiteDatabase db = getReadableDatabase();
        Cursor cursor = db.rawQuery("SELECT * FROM " + BLINDWALLTABLE, null);
        if (cursor != null && cursor.moveToFirst()) {
            do {
                // String qryCreateBlindWallPoint = "CREATE TABLE BlindWallPoint(Id INT PRIMARY KEY, Name TEXT, Info TEXT, Latitude FLOAT, Longitude FLOAT, Author TEXT, Year INT);";
                int id = cursor.getInt(0);
                String name = cursor.getString(1);
                String info = cursor.getString(2);
                float lat = cursor.getFloat(3);
                float lon = cursor.getFloat(4);
                String author = cursor.getString(5);
                int year = cursor.getInt(6);

                BlindWallPoint point = new BlindWallPoint(id, name, info, lon, lat, false, author, year);
                points.add(point);
            } while(cursor.moveToNext());
            cursor.close();
        }
        db.close();
        return points;
    }

    public List<String> getImagesByBlindWallId(int wallId) {
        List<String> urls = new ArrayList<>();
        SQLiteDatabase db = getReadableDatabase();
        Cursor cursor = db.rawQuery("SELECT * FROM " + BLINDWALLIMAGETABLE + " WHERE Id = " + wallId, null);
        if (cursor != null && cursor.moveToFirst()) {
            do {
                // String qryCreateBlindWallPoint = "CREATE TABLE BlindWallPoint(Id INT PRIMARY KEY, Name TEXT, Info TEXT, Latitude FLOAT, Longitude FLOAT, Author TEXT, Year INT);";
                String url = cursor.getString(1);
                urls.add(url);
            } while(cursor.moveToNext());
            cursor.close();
        }
        db.close();
        return urls;
    }

