    public List<RoutePoint> allRoutePoints() {
        List<RoutePoint> allRoutePoints = new ArrayList<>();
        SQLiteDatabase db = getReadableDatabase();
        Cursor cursor = db.rawQuery("SELECT * FROM " + ROUTEPOINTTABLE + " ORDER BY Id ASC", null);
        if (cursor != null && cursor.moveToFirst()) {
            do {
                // String qryCreateRoutePoint = "CREATE TABLE RoutePoint(Id INT PRIMARY KEY, Latitude FLOAT, Longitude FLOAT, NaamNL TEXT, NaamEng TEXT, DescNL TEXT, DescEng TEXT);";
                int id = cursor.getInt(0);
                float lat = cursor.getFloat(1);
                float lon = cursor.getFloat(2);
                String nameNl = cursor.getString(3);
                String nameEng = cursor.getString(4);
                String descNl = cursor.getString(5);
                String descEng = cursor.getString(5);
                RoutePoint point = new RoutePoint(id, nameNl, nameEng, descNl, descEng, lon, lat, false);
                allRoutePoints.add(point);
            } while(cursor.moveToNext());
            cursor.close();
        }
        db.close();
        return allRoutePoints;
    }

    public List<String> getImagesByPointId(int pointId) {
        List<String> urls = new ArrayList<>();
        SQLiteDatabase db = getReadableDatabase();
        Cursor cursor = db.rawQuery("SELECT * FROM " + ROUTEPOINTIMAGETABLE + " WHERE Id = " + pointId, null);
        if (cursor != null && cursor.moveToFirst()) {
            do {
                String url = "img" + cursor.getString(1);
                urls.add(url);
            } while(cursor.moveToNext());
            cursor.close();
        }
        db.close();
        return urls;
    }

    public List<BlindWallPoint> allBlindWallPoints() {
        List<BlindWallPoint> points = new ArrayList<>();
        SQLiteDatabase db = getReadableDatabase();
        Cursor cursor = db.rawQuery("SELECT * FROM " + BLINDWALLTABLE, null);
        if (cursor != null && cursor.moveToFirst()) {
            do {
                String qryCreateBlindWallPoint = "CREATE TABLE BlindWallPoint(Id INT PRIMARY KEY, NameNL TEXT, NameEng TEXT, InfoNL TEXT, InfoEng TEXT, Latitude FLOAT, Longitude FLOAT, Author TEXT, Year INT);";
                int id = cursor.getInt(0);
                String nameNl = cursor.getString(1);
                String nameEng = cursor.getString(2);
                String infoNl = cursor.getString(3);
                String infoEng = cursor.getString(4);
                float lat = cursor.getFloat(5);
                float lon = cursor.getFloat(6);
                String author = cursor.getString(7);
                int year = cursor.getInt(8);

                BlindWallPoint point = new BlindWallPoint(id, nameNl, nameEng, infoNl, infoEng, lon, lat, false, author, year);
                points.add(point);
            } while(cursor.moveToNext());
            cursor.close();
        }
        db.close();
        return points;
    }

    public List<String> getImagesByBlindWallId(int wallId) {
        List<String> urls = new ArrayList<>();
        SQLiteDatabase db = getReadableDatabase();
        Cursor cursor = db.rawQuery("SELECT * FROM " + BLINDWALLIMAGETABLE + " WHERE Id = " + wallId, null);
        if (cursor != null && cursor.moveToFirst()) {
            do {
                String url = "https://api.blindwalls.gallery/" + cursor.getString(1);
                urls.add(url);
            } while(cursor.moveToNext());
            cursor.close();
        }
        db.close();
        return urls;
    }

